import React,{useEffect,useState} from "react";
import {Link} from "react-router-dom";
import Header from "../components/Main/Header";
import SearchListing from '../components/SearchResults/SearchListing';
import NoMatches from "../components/SearchResults/NoMatches";
import Error404 from './Error404';
import "./SearchResult.css"
import Headernew from "../components/Main/headernew"

const SearchResult = props => {

  const [isLoading, setIsLoading] = useState(false);
  const [matchedListings,setMatchedListings] = useState();
  let query = props.location.search;

  let searchphrase = query.substring(8);
  // searchphrase = what the user typed
  searchphrase = searchphrase.toLowerCase().replace(/(<([^>]+)>)/ig,"")

  useEffect(()=>{
    setIsLoading(true);

    // call express api to get search results 
    const getListings = async () =>{
      try{
        // returns an array of listings
        let response = await fetch(`http://localhost:5000/api/search/keyword/switch`);
        setMatchedListings(response.json().listingsToDisplay);
        console.log(matchedListings);

      } catch(err){
        // Todo: Add Error Handling if API call fails
        console.log(err);
      }
      setIsLoading(false);
    }
    getListings();

  },[]);

    return (
      <div style= {{width:"100%"}} >
        <Header />
        {!isLoading && matchedListings &&
        <div>Hello</div>
        }
      </div>

    );

};

export default SearchResult;
